package package1;

public class AsstTempScale {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
     double c =0.0;
     System.out.println("Input: " +c);
     //System.out.println("convert celsius into Fahrenheit");
     System.out.println();
     double F = ((1.8*c)+32);
     System.out.println("Output: ");
     System.out.println(c +" Celsius= " +F +" Fahrenheit");
     
    
     //System.out.println("convert celsius into kelvin");
     double k = (c+273);
     System.out.println(c +" Celsius= " +k +" Kelvin");
		
	}

}
