package package1;

public class AsstWhile2InfiniteLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=1;
		while(i!=0)
		{
			System.out.println("while loop  1st infinite method");
			i++;
		}
		int j=1;
		while(j>0)
		{
			System.out.println("2nd infinite method");
			j++;
		}
	}

}
